ba(255, 255, 255, 0.8);}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective:before {background: #F26328;}&lt;br /&gt;&amp;nbsp;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body h3 { color: #F26328;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .post-content {transform: rotateY(-5deg);-webkit-transform: rotateY(-5deg);-moz-transform: rotateY(-5deg);-o-transform: rotateY(-5deg);-ms-transform: rotateY(-5deg);}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body {border-color: #F26328;box-shadow: 10px 0px 10px -6px rgba(0, 0, 0, 0.1);}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body p {max-height: 460px; color: rgba(0,0,0,0.6); transition-delay: 0s, 0.6s;-webkit-transition-delay: 0s, 0.6s;-moz-transition-delay: 0s, 0.6s;-o-transition-delay: 0s, 0.6s;-ms-transition-delay: 0s, 0.6s;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body:before {color: #F26328;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .thumb {transform: scale(1);box-shadow: 0 0 0 8px rgba(242,99,40,1), 0 1px 1px rgba(255,255,255,0.5); -webkit-transform: scale(1);-moz-transform: scale(1);-o-transform: scale(1);-ms-transform: scale(1);}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .thumb span {color: #F26328;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .thumb:before {&lt;br /&gt;background: transparent url(data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAoAAAAICAYAAADA+m62AAAAPUlEQVQYV2NkQAOfUjX+882+wYgujiIAU4RNMVwhuiQ6H6wQl3XI4oy4FMHcCJPHcDS6J2A2EqUQpJhohQBbNyaHFmzEqgAAAABJRU5ErkJggg==);}&lt;br /&gt;@media screen and (max-width: 850px) {.blog-post-body h3 {font-size: 20px;}&lt;br /&gt;&amp;nbsp;.blog-post-body p { font-size: 14px; text-align: justify; }&lt;br /&gt;&amp;nbsp;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body p { max-height: 500px; }}&lt;br /&gt;@media screen and (max-width: 540px) {.timeline:before { left: 50px;}&lt;br /&gt;.event {padding-right: 0px; margin-bottom: 100px; }&lt;br /&gt;&amp;nbsp;.thumb { transform: scale(0.8); -webkit-transform: scale(0.8);-moz-transform: scale(0.8);-o-transform: scale(0.8);-ms-transform: scale(0.8);}&lt;br /&gt;&amp;nbsp;.event input[type=&amp;amp;quot;radio&amp;amp;quot;] { width: 100px;height: 100px;left: 0px;top: 0px; }&lt;br /&gt;.thumb:before,.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .thumb:before {background: none;width: 0;}&lt;br /&gt;&amp;nbsp;.event label {display: none;}&lt;br /&gt;&amp;nbsp;.content-perspective {margin-left: 0px;top: 80px;}&lt;br /&gt;&amp;nbsp;.content-perspective:before {height: 0px; }&lt;br /&gt;&amp;nbsp;.post-content { transform: rotateX(-10deg);-webkit-transform: rotateX(-10deg);-moz-transform: rotateX(-10deg);-o-transform: rotateX(-10deg);-ms-transform: rotateX(-10deg);}&lt;br /&gt;&amp;nbsp;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .post-content { transform: rotateX(10deg);-webkit-transform: rotateX(10deg);-moz-transform: rotateX(10deg);-o-transform: rotateX(10deg);-ms-transform: rotateX(10deg);}&lt;br /&gt;.blog-post-body {border-left: none;border-top: 5px solid #8995A1;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body {border-color: #F26328;box-shadow: 0 10px 10px -6px rgba(0, 0, 0, 0.1);}&lt;br /&gt;.blog-post-body:before {content: &amp;amp;#39;\\25b4&amp;amp;#39;;left: 33px; top: -32px;}&lt;br /&gt;.event input[type=&amp;amp;quot;radio&amp;amp;quot;]:checked ~ .content-perspective .blog-post-body p {max-height:300px; }}&lt;br /&gt;ul.timeline {list-style-type: none;}&lt;br /&gt;&amp;lt;/style&amp;gt; &lt;br /&gt;&amp;lt;/b:if&amp;gt;&lt;br /&gt;&amp;lt;/b:if&amp;gt;&lt;/div&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 5. &lt;/b&gt;&lt;/span&gt;Search for the SECOND instance of this code below (hit Enter two times):&lt;br /&gt;&lt;blockquote class=&quot;tr_bq&quot;&gt;&amp;lt;data:post.body/&amp;gt;&lt;/blockquote&gt;&lt;br /&gt;&lt;b&gt;Note:&lt;/b&gt; depending on the template you are using, you could find more lines like this one above. Therefore, if you don&#039;t see any changes after saving the template, try to replace the last one, as well.&lt;br /&gt;&lt;br /&gt;Also, please note that if you are using the Read more function, you may need to remove it in order to make it work.&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 6.&lt;/b&gt;&lt;/span&gt; After you found &lt;span style=&quot;background-color: yellow;&quot;&gt;&amp;lt;data:post.body/&amp;gt;&lt;/span&gt; - replace it this code:&lt;br /&gt;&lt;blockquote class=&quot;tr_bq&quot;&gt;&amp;lt;b:if cond=&#039;data:blog.pageType != &amp;amp;quot;static_page&amp;amp;quot;&#039;&amp;gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:blog.pageType != &amp;amp;quot;item&amp;amp;quot;&#039;&amp;gt;&lt;br /&gt;&amp;lt;ul class=&#039;timeline&#039;&amp;gt;&amp;lt;li class=&#039;event&#039;&amp;gt;&amp;lt;input checked=&#039;checked&#039; name=&#039;tl-group&#039; type=&#039;radio&#039;/&amp;gt;&amp;lt;label/&amp;gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:post.thumbnailUrl&#039;&amp;gt;&amp;lt;img class=&#039;thumb&#039; expr:alt=&#039;data:post.title&#039; expr:src=&#039;data:post.thumbnailUrl&#039;/&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;div class=&#039;thumb&#039;&amp;gt;&amp;lt;b:if cond=&#039;data:post.dateHeader&#039;&amp;gt;&amp;lt;span&amp;gt;&amp;lt;data:post.dateHeader/&amp;gt;&amp;lt;/span&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/div&amp;gt;&lt;br /&gt;&amp;lt;div class=&#039;content-perspective&#039;&amp;gt;&amp;lt;div class=&#039;post-content&#039;&amp;gt;&amp;lt;div class=&#039;blog-post-body&#039;&amp;gt;&amp;lt;h3&amp;gt;&amp;lt;b:if cond=&#039;data:post.link&#039;&amp;gt;&amp;lt;a expr:href=&#039;data:post.link&#039;&amp;gt;&amp;lt;data:post.title/&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;b:else/&amp;gt;&amp;lt;b:if cond=&#039;data:post.url&#039;&amp;gt;&amp;lt;b:if cond=&#039;data:blog.url != data:post.url&#039;&amp;gt;&amp;lt;a expr:href=&#039;data:post.url&#039;&amp;gt;&amp;lt;data:post.title/&amp;gt;&amp;lt;/a&amp;gt;&amp;lt;b:else/&amp;gt;&amp;lt;data:post.title/&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;b:else/&amp;gt;&amp;lt;data:post.title/&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/h3&amp;gt;&amp;lt;p&amp;gt;&amp;lt;b:if cond=&#039;data:blog.pageType != &amp;amp;quot;item&amp;amp;quot;&#039;&amp;gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:blog.pageType != &amp;amp;quot;static_page&amp;amp;quot;&#039;&amp;gt;&amp;lt;data:post.snippet/&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/p&amp;gt;&amp;lt;/div&amp;gt;&amp;lt;/div&amp;gt;&amp;lt;/div&amp;gt;&amp;lt;/li&amp;gt;&amp;lt;/ul&amp;gt;&amp;lt;/b:if&amp;gt;&amp;lt;/b:if&amp;gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:blog.pageType == &amp;amp;quot;item&amp;amp;quot;&#039;&amp;gt;&amp;lt;data:post.body/&amp;gt;&amp;lt;/b:if&amp;gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:blog.pageType == &amp;amp;quot;static_page&amp;amp;quot;&#039;&amp;gt;&amp;lt;data:post.body/&amp;gt;&amp;lt;/b:if&amp;gt;&lt;/blockquote&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 7.&lt;/b&gt; &lt;/span&gt;Finally, click on the &lt;b&gt;Save template&lt;/b&gt; button to save the changes.&lt;br /&gt;&lt;br /&gt;Now, we will need to make some small adjustments in order to make the posts date appear correctly:&lt;br /&gt;Go to &lt;b&gt;Layout&lt;/b&gt; and click on the &lt;b&gt;Edit&lt;/b&gt; link just below the Blog posts area. When the pop-up window appears, check the date option and choose to display the date in the following order (date/month/year):&lt;br /&gt;&lt;br /&gt;&lt;div class=&quot;separator&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;Note that you can also hide the posts date by unchecking this option.&lt;br /&gt;Finally, click on the &lt;b&gt;Save&lt;/b&gt; button and that&#039;s it! View your blog and enjoy this Responsive Timeline view with 3D Effect on Blogger posts. If you need more help, please leave a comment below.&lt;br /&gt;&lt;br /&gt;Credit: ', 'admin', 'post', 4, '0000-00-00 00:00:00', '1'),
(24, '2014-10-18 14:39:54', 'How to Enable Google Plus Comments in Blogger', 'how-to-enable-google-plus-comments-in-blogger', '&lt;h2&gt;How to Enable Google Plus Comments in Blogger&lt;/h2&gt;Long time ago, Google proudly announced their new integrated commenting system which is now available for users who have created a Google+ profile and connected it with their Blogspot blogs. This way, Blogger users will be able to use Google+ as a commenting system for their blogs, while comments from Google+ will automatically appear on their blogs. &lt;br /&gt;&lt;div class=&quot;ssmainhide&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;For those who haven&#039;t yet upgraded the Blogger profile to a Google+ profile, here&#039;s a tutorial on . Once you have connected a blog to a Google+ profile, you&#039;ll have a new &quot;&lt;b&gt;Use Google+ Comments on this blog&lt;/b&gt;&quot; setting on the Google+ tab of your blog. To enable the Google plus commenting system, just check the box next to this setting:&lt;br /&gt;&lt;br /&gt;&lt;div class=&quot;separator&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;As soon as you&#039;ve enabled the feature through your Blogger Dashboard, you&#039;ll have the following features:&lt;br /&gt;&lt;br /&gt;- &lt;b&gt;Threaded commenting system&lt;/b&gt;: the threaded system will allow a reader to reply to other comments on that post, thus the conversations will become much easier, more effective and more enjoyable&lt;br /&gt;- &lt;b&gt;Public and private comments&lt;/b&gt;: this ensures a better privacy for your visitors which can make their comments either public or private&lt;br /&gt;- &lt;b&gt;Edit or delete comments&lt;/b&gt;: your visitors will be able to edit any comments that they wrote even after publishing, so they don&#039;t have to write another comment  explaining the correction&lt;br /&gt;- &lt;b&gt;Google Plus One (+1) button in comments&lt;/b&gt;: we can up-vote any comment by clicking this button. This is a great feature which could also help you to get some traffic from Google+&lt;br /&gt;- &lt;b&gt;&quot;Also share on Google+&quot; check box&lt;/b&gt;: with this option which is right below the comment editor, we will be able to share a specific comment on Google+ and thus get more traffic!&lt;br /&gt;&lt;br /&gt;&lt;h3&gt;Things to consider before adding the Google Plus Comments in Blogger:&lt;/h3&gt;- the Google Plus commenting system won&#039;t work for private and adult blogs&lt;br /&gt;- If you are using a third-party commenting system like Disqus, your comments might not be retained when you enable Google+ Comments&lt;br /&gt;- If you change domain name, comments will be gone. Therefore, it would be highly recommended to implement the Google Plus commenting system only after you&#039;ve decided to use a custom domain name &lt;br /&gt;- If you choose to allow comments pending moderation, you will have to visit the post in order to  approve, hide or delete comments before they are publicly visible&lt;br /&gt;- Only registered Google+ users can comment, this means that visitors who don&#039;t have a Google+ account will not be able to comment on your blog&lt;br /&gt;&lt;br /&gt;&lt;h2&gt;How to Enable Google Plus on Custom Blogger Templates&lt;/h2&gt;&lt;br /&gt;If you are using a custom blogger template, the comments might not show up after checking the &quot;&lt;b&gt;Use Google+ Comments on this blog&lt;/b&gt;&quot; setting. In this case, we will need to apply the following trick in order to enable it on custom templates:&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 1. &lt;/b&gt;&lt;/span&gt;From the , go to &lt;b&gt;Template&lt;/b&gt; and click on the &lt;b&gt;Edit HTML&lt;/b&gt; button.&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 2.&lt;/b&gt;&lt;/span&gt; Click anywhere inside the code area and press the Ctrl + F keys to open the Blogger search box.&lt;br /&gt;Type or paste the following line inside the search box and hit Enter to find it:&lt;br /&gt;&lt;blockquote class=&quot;tr_bq&quot;&gt;&amp;lt;div class=&#039;post-footer&#039;&amp;gt;&lt;/blockquote&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 3.&lt;/b&gt;&lt;/span&gt; Just below it, paste this code:&lt;br /&gt;&lt;blockquote class=&quot;tr_bq&quot;&gt;&amp;lt;div class=&#039;cmt_iframe_holder&#039; data-viewtype=&#039;FILTERED_POSTMOD&#039; expr:href=&#039;data:blog.canonicalUrl&#039;/&amp;gt;&lt;/blockquote&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 4.&lt;/b&gt;&lt;/span&gt; Click on the &lt;b&gt;Save template&lt;/b&gt; button to save the changes... and that&#039;s it. Now we should be able to enjoy the Google Plus comments in our Blogspot blogs.&lt;br /&gt;&lt;br /&gt;Happy commenting!', 'admin', 'post', 4, '0000-00-00 00:00:00', '1'),
(25, '2014-10-27 15:45:01', 'Display Blogger Posts in Grid View with Thumbnails', 'display-blogger-posts-in-grid-view-with-thumbnails', 'Grid View with Thumbnails is a script for self-hosted Blogger blogs which will display blog posts as a thumbnail grid of images in homepage and archive pages. Instead of sending your blog visitors to a page that displays all the posts in full length with a large image which takes up too much space and requires too much scrolling, now you could have a clean page that displays a gallery grid, with thumbnails and post titles, linking back to the source post for that thumbnail. Let&#039;s take a look at what it could look like in this .&lt;br /&gt;&lt;br /&gt;&lt;div class=&quot;separator&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;If you have a wallpaper or photo blog that would benefit from displaying a thumbnail grid style layout, look no further. Here&#039;s how you can add Grid (gallery) View to Blogger posts.&lt;br /&gt;&lt;br /&gt;&lt;h2&gt;Adding Grid (Gallery) View to Blogger Posts&lt;/h2&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 1.&lt;/b&gt;&lt;/span&gt; Log in to your Blogger account and go to &lt;b&gt;Template&lt;/b&gt; &amp;gt; click on the &lt;b&gt;Edit HTML&lt;/b&gt; button&lt;br /&gt;&lt;br /&gt;&lt;div class=&quot;separator&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 2.&lt;/b&gt;&lt;/span&gt; Click anywhere inside the code area and press the CTRL + F keys to open the search box&lt;br /&gt;&lt;br /&gt;&lt;div class=&quot;separator&quot; style=&quot;clear: both; text-align: center;&quot;&gt;&lt;/div&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 3.&lt;/b&gt;&lt;/span&gt; Type &lt;span style=&quot;background-color: yellow;&quot;&gt;&amp;lt;/head&amp;gt;&lt;/span&gt; inside the search box and hit Enter to find it.&lt;br /&gt;&lt;br /&gt;&lt;span style=&quot;color: #3d85c6;&quot;&gt;&lt;b&gt;Step 4.&lt;/b&gt;&lt;/span&gt; Just above the &lt;span style=&quot;background-color: yellow;&quot;&gt;&amp;lt;/head&amp;gt;&lt;/span&gt; tag, add the following script and style:&lt;br /&gt;&lt;blockquote class=&quot;tr_bq&quot;&gt;&lt;span style=&quot;color: #6aa84f;&quot;&gt;&lt;b&gt;&amp;lt;script src=&quot;http://code.jquery.com/jquery-1.9.1.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;br /&gt;&amp;lt;script src=&quot;http://code.jquery.com/jquery-migrate-1.1.0.js&quot;&amp;gt;&amp;lt;/script&amp;gt;&lt;/b&gt;&lt;/span&gt;&lt;br /&gt;&amp;lt;b:if cond=&#039;data:blog.pageType == &amp;amp;quot;index&amp;amp;quot;&#039;&amp;gt;&lt;br /&gt;&amp;lt;script type=&#039;text/javascript&#039;&amp;gt;//&amp;lt;![CDATA[&lt;br /&gt;$(document).ready(function() {&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var width = &lt;span style=&quot;color: blue;&quot;&gt;&lt;b&gt;200&lt;/b&gt;&lt;/span&gt;;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var height = &lt;span style=&quot;color: blue;&quot;&gt;&lt;b&gt;170&lt;/b&gt;&lt;/span&gt;;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var placeholder = &#039;&lt;span style=&quot;color: #cc0000;&quot;&gt;http://3.bp.blogspot.com/-7Ie-cUwxWB4/U2KIRWgvpNI/AAAAAAAAIfU/Mj6qpg5S2fk/s1600/no-thumb.png&lt;/span&gt;&#039;;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var margins = &quot;0px 0px 10px 10px&quot;;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var fitThumb = 1;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var titleTopPadding = 5;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var titleBottomPadding = 8;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var titleLeftRightPadding = 5;&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var titlePadding = titleTopPadding + &#039;px &#039; + titleLeftRightPadding + &#039;px &#039; + titleBottomPadding + &#039;px &#039; + titleLeftRightPadding + &#039;px&#039;;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $(&#039;.post-body&#039;).each(function(n, wrapper) {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var wrapper = $(wrapper);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var image = $(wrapper).find(&#039;img&#039;).first();&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var link = wrapper.parent().find(&#039;h3 a&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var linkURL = link.attr(&#039;href&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var linkTitle = link.text();&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; $(link).remove();&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; wrapper.empty();&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; if (image.attr(&#039;src&#039;)) {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var thumbHeight = image.attr(&#039;height&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var thumbWidth = image.attr(&#039;width&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var thumbParent = $(image).parent();&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; wrapper.append(thumbParent);&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; if (fitThumb) {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; image.attr({&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; src: image.attr(&#039;src&#039;).replace(/s\\B\\d{3,4}/, &#039;s&#039; + width + &#039;-c&#039;)&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; });&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; image.attr(&#039;width&#039;, width).attr(&#039;height&#039;, height);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; } else {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; image.attr({&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; src: image.attr(&#039;src&#039;).replace(/s\\B\\d{3,4}/, &#039;s&#039; + width)&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; });&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; image.attr(&#039;width&#039;, width);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var changeHeight = (thumbHeight / thumbWidth * width).toFixed(0);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; image.attr(&#039;height&#039;, changeHeight);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; } else {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var image = $(&#039;&amp;lt;img&amp;gt;&#039;).attr(&#039;src&#039;, placeholder).attr(&#039;height&#039;, height).attr(&#039;width&#039;, width);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var thumbParent = $(&#039;&amp;lt;a&amp;gt;&#039;).append(image).appendTo(wrapper);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; thumbParent.attr(&#039;href&#039;, linkURL).css(&#039;clear&#039;, &#039;none&#039;).css(&#039;margin-left&#039;, &#039;0&#039;).css(&#039;margin-right&#039;, &#039;0&#039;).addClass(&#039;postThumbnail&#039;);&lt;br /&gt;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var thumbTitle = $(&#039;&amp;lt;div&amp;gt;&#039;).prepend(linkTitle).css(&#039;padding&#039;, titlePadding).css(&#039;opacity&#039;, &#039;0.9&#039;).css(&#039;filter&#039;, &#039;alpha(opacity=0.9)&#039;).css(&#039;width&#039;, width).appendTo(thumbParent);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var ptitleHeight = thumbTitle.height();&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var summary = parseInt(ptitleHeight) + parseInt(titleTopPadding) + parseInt(titleBottomPadding);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; thumbTitle.css(&#039;margin-top&#039;, &#039;-&#039; + summary + &#039;px&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; wrapper.css(&#039;float&#039;, &#039;left&#039;).css(&#039;height&#039;, height).css(&#039;width&#039;, width).css(&#039;margin&#039;, margins).css(&#039;overflow&#039;, &#039;hidden&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; });&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; $(&#039;#blog-pager&#039;).css(&#039;clear&#039;, &#039;both&#039;);&lt;br /&gt;});&lt;br /&gt;&lt;br /&gt;function hideLightbox() {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; var images = document.getElementsByTagName(&#039;img&#039;);&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; for (var i = 0; i &amp;lt; images.length;++i) {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; images[i].onmouseover = function() {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; var html = this.parentNode.innerHTML;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; this.parentNode.innerHTML = html;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; this.onmouseover = null;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp;&amp;nbsp; };&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; }&lt;br /&gt;}&lt;br /&gt;&lt;br /&gt;if (window.addEventListener) {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; window.addEventListener(&#039;load&#039;, hideLightbox, undefined);&lt;br /&gt;} else {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; window.attachEvent(&#039;onload&#039;, hideLightbox);&lt;br /&gt;}&lt;br /&gt;//]]&amp;gt;&lt;br /&gt;&amp;lt;/script&amp;gt;&lt;br /&gt;&amp;lt;style&amp;gt;&lt;br /&gt;.post {&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; border-bottom: 0 dotted #E6E6E6;&lt;br /&gt;&amp;nbsp;&amp;nbsp;&amp;nbsp; margin-bottom: 0;&lt;br /&gt